import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import bean1 from './img/bean1.png';
import bean2 from './img/bean2.png';
import cup from './img/cup.png';

class Cup extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
    	count: 15,
    }
  }

  render() {

  	let beanList = [];
  	let left = 62;
  	let top = 258;
  	for (let i = 0; i < this.state.count; i++) { 
	  beanList.push(<Bean key={i} topPx={top} leftPx={left} />);
	  left += 30; 
	  if (left > 200) {
	  	left = (i % 2 == 0) ? (62) : ((i % 3) ? (52) : (72));
	  	top -= 20;
	  	if (top < 75) {
	  		//full cup
	  		break;
	  	}
	  }
	}

    return (
      <div className="movie-cover">
      	<img src={cup} alt="cup" />
      	{beanList}
      </div>
    );
  }
}

class Bean extends React.Component {

	constructor(props) {
	    super(props);
	    this.state = {
	    }
	}

	randomBean() {
		const beans = [bean1, bean2];
		return beans[Math.floor(Math.random() * 2)];  
	}

	render() {

	    return (
	      <img src={this.randomBean()} alt="bean" width="40" height="32" style={{"top" : this.props.topPx, "left" : this.props.leftPx}} />
	    );
	}
}

export default Cup;
